
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println("With Integer values:");
		
		//Plus operator
		
		int vp1 = 20;
		int vp2 = 5;
		
		int plus = vp1 + vp2;
		
		System.out.println(vp1 + " + " + vp2 + " = " + plus);
		
		//Minus operator
		
		int vm1 = 25;
		int vm2 = 15;
		
		int minus = vm1 - vm2;
		
		System.out.println(vm1 + " - " + vm2 + " = " + minus);
		
		//Multiply operator
		int vmp1 = 25;
		int vmp2 = 15;
		
		int multiply = vmp1 * vmp2;
		
		System.out.println(vmp1 + " * " + vmp2 + " = " + multiply);

		//Divide operator
		int vd1 = 25;
		int vd2 = 15;
		
		int divide = vd1 / vd2;
		
		System.out.println(vd1 + " / " + vd2 + " = " + divide);

		//Modulus operator. Returns what is left of the division of two numbers.
		int vms1 = 25;
		int vms2 = 15;
		
		int modulus = vms1 % vms2;
		
		System.out.println(vms1 + " % " + vms2 + " = " + modulus);

		//Over again with double values. 
		
		System.out.println("With double values:");
		
		//Plus operator
		
		double dvp1 = 20.6;
		double dvp2 = 5.0;
		
		double dplus = dvp1 + dvp2;
		
		System.out.println(dvp1 + " + " + dvp2 + " = " + dplus);
		
		//Minus operator
		
		double dvm1 = 25.9;
		double dvm2 = 15.8;
		
		double dminus = dvm1 - dvm2;
		
		System.out.println(dvm1 + " - " + dvm2 + " = " + dminus);
		
		//Multiply operator
		double dvmp1 = 25.45;
		double dvmp2 = 15.67;
		
		double dmultiply = dvmp1 * dvmp2;
		
		System.out.println(dvmp1 + " * " + dvmp2 + " = " + dmultiply);

		//Divide operator
		double dvd1 = 25.9;
		double dvd2 = 15.7;
		
		double ddivide = dvd1 / dvd2;
		
		System.out.println(dvd1 + " / " + dvd2 + " = " + ddivide);

		//Modulus operator. Returns what is left of the division of two numbers.
		double dvms1 = 25.7;
		double dvms2 = 15.6;
		
		double dmodulus = dvms1 % dvms2;
		
		System.out.println(dvms1 + " % " + dvms2 + " = " + dmodulus);
		

		
		
		
	}

}
